CREATE TABLE "public"."response_tokens" ("survey_id" Integer NOT NULL, "contract_address" text NOT NULL, "wallet" text NOT NULL, "tokens" integer NOT NULL, "chain" text NOT NULL, PRIMARY KEY ("survey_id","contract_address","wallet") , FOREIGN KEY ("survey_id") REFERENCES "public"."surveys"("id") ON UPDATE no action ON DELETE no action, FOREIGN KEY ("contract_address", "survey_id", "chain") REFERENCES "public"."contracts"("address", "survey_id", "chain") ON UPDATE no action ON DELETE no action, FOREIGN KEY ("wallet") REFERENCES "public"."users"("wallet") ON UPDATE no action ON DELETE no action);
